properties.js:
function (assets) {
    var getCookie = function (cName) {
        var cookval = '';
        var i, x, y, ARRcookies = document.cookie.split(";");

        for (i = 0; i < ARRcookies.length; i++) {
            x = ARRcookies[i].substr(0, ARRcookies[i].indexOf("="));
            y = ARRcookies[i].substr(ARRcookies[i].indexOf("=") + 1);
            x = x.replace(/^\s+|\s+$/g, "");
            if (x == cName) {
                cookval = unescape(y);
            }
        }
        return cookval;
    };

    if (getCookie('asuDockViewed') == 'true') {
        // Expand dock
        let dockElem = document.querySelector('.MAG_web_sub_asu_b2b-custom-registered-access');
        dockElem.classList.remove('expanded');

        // Show header on collapsed dock
        let headerText = document.querySelector("[data-testid='expanded-dock-heading-selector'] div div div div");
        headerText.style.opacity = '1';

        // Disable pointer events, except on close buttom
        dockElem.style.pointerEvents = 'none';
        let closeButton = document.querySelector("[data-testid='expanded-dock-btn-selector']");
        closeButton.style.pointerEvents = 'auto';
    } else {
        // Sets a cookie called asuDockViewed that expires in 24 hours, so that dock only expands once everyday.
        document.cookie = 'asuDockViewed=true; domain=.nytimes.com; path=/; max-age=' + 60 * 60 * 24;
    }

    let dockElem = document.querySelector('.MAG_web_sub_asu_b2b-custom-registered-access');
    dockElem.style.display = 'block';
}

