$bgColor: #FFD2C4;
$ctaColor: #FF6200;
$copyColor: #FF6100;


a {
  color: inherit; /* blue colors for links too */
  text-decoration: inherit; /* no underline */
}

@media screen and (-ms-high-contrast: active), screen and (-ms-high-contrast: none) {
  /* IE10+ specific styles go here */
  .welcomeAdLayout__image__wrap {
    min-height: 1px;
  }
  .welcomeAdLayout__content__inner {
    min-height: 1px;
  }
}

.welcomeAdLayout {
  height: 425px;
  background-color: $bgColor !important;
}
@media screen and (min-width: 768px) {
  .welcomeAdLayout {
    height: 320px;
  }
}
.welcomeAdLayout__content__inner {
  height: 100%;
}
.welcomeAdLayout__content {
  width: 100%;
  height: 100%;
}
.welcomeAdLayout__text {
  margin: 0;
  width: 100%;
  height: 100%;
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: space-between;

  @media screen and (min-width: 768px) {
    flex-direction: row;
  }
  .welcomeAd__main__content {
    margin: 0;
    width: 100%;
    height: 100%;
    display: flex;
    align-items: center;
    flex-direction: column;
    align-items: center;
    justify-content: space-evenly;
    padding-top: 20px;

    @media screen and (min-width: 768px) {
      flex-direction: row;
      padding-top: 0;
    }
  }
}

.welcomeAdLayout__Login {
  font-size: 7px;
  line-height: 8px;
  color: #000;
  a {
    color: #000;
  }
  position: absolute;
  top: 19px;
  right: 19px;
  @media screen and (min-width: 768px) {
    position: absolute;
    top: 22px;
    right: 22px;
    font-size: 11px;
    line-height: 11px;
  }
