function (assets) {
    var getCookie = function (cName) {
        var cookval = '';
        var i, x, y, ARRcookies = document.cookie.split(";");

        for (i = 0; i < ARRcookies.length; i++) {
            x = ARRcookies[i].substr(0, ARRcookies[i].indexOf("="));
            y = ARRcookies[i].substr(ARRcookies[i].indexOf("=") + 1);
            x = x.replace(/^\s+|\s+$/g, "");
            if (x == cName) {
                cookval = unescape(y);
            }
        }
        return cookval;
    };

    if (getCookie('holidayGiftDockViewed') == 'true') {
        // Expand dock
        let dockElem = document.querySelector('.MAG_web_subs_INYT_holiday-gifting');
        dockElem.classList.remove('expanded');

        // Show header on collapsed dock
        let headerText = document.querySelector("[data-testid='expanded-dock-heading-selector'] div div div div");
        headerText.style.opacity = '1';

        // Add clickthrough event on "standard" dock
        dockElem.onclick = function(event) {
            let closeButton = document.querySelector("[data-testid='expanded-dock-btn-selector']");
            if (!(event.target === closeButton || closeButton.contains(event.target))) {
                window.location = 'https://www.nytimes.com/subscription/gift?campaignId=9XRUX';
            }
        }
    } else {
        // Sets a cookie so that dock only expands once per week.
        document.cookie = 'holidayGiftDockViewed=true; domain=.nytimes.com; path=/; max-age=' + 60 * 60 * 168;
    }

    let dockElem = document.querySelector('.MAG_web_subs_INYT_holiday-gifting');
    dockElem.style.display = 'block';
}

