.dockCard {
    height: 140px;
}

.dockCard__content {
    height: 100%;
    box-sizing: border-box;
    padding: 20px 15px;
    flex-direction: column;
    align-items: center;

    @media screen and (max-width: 767px) { 
        display: flex;
        justify-content: space-between;
    }

    @media screen and (min-width: 767px) { 
        padding: 25px;
        flex-direction: row;
    }

    @media screen and (min-width: 800px) { 
        padding: 35px;
    }

    @media screen and (min-width: 1024px) { 
        padding: 50px 40px !important;
    }
}

.dockCard__content__inner {
    align-items: flex-start !important;
}

.dockCard__text {
    font-size: 16px;
    line-height: 20px;

    @media screen and (min-width: 767px) {
        font-size: 18px;
        line-height: 22px;
    }

    @media screen and (min-width: 1024px) {
        font-size: 21px;
        line-height: 25px;
    }
}

.dockCard__image__wrap {
    align-self: flex-start !important;
}

.dockCard__button__wrap {
    @media screen and (max-width: 767px) {
        align-self: flex-start !important;
    }
}

.dockCard__button {
    font-weight: bold;
    width: 70px;
    font-size: 10px;
    line-height: 12px;

    @media screen and (min-width: 767px) {
        width: 90px;
        font-size: 12px;
        line-height: 14px;
    }
}

.nowrap {
    white-space: nowrap;
}

