#vi_welcome_close {
  position: absolute;
  background-clip: padding-box;
  margin: 0;
  padding: 0;
  box-shadow: 0 0 0 1px #000, 0 0 0 2px #000, -1px 1px 2px 3px rgba(0, 0, 0, 0);
  border: none;
  background-color: #000;
  width: 22px;
  height: 22px;
  right: 18px;
  top: 14px;
  cursor: pointer;
  line-height: normal;
  vertical-align: middle;
  z-index: 10;
}

#vi_welcome_close:hover {
  background-color: #000;
}

#vi_welcome_close:before {
  -webkit-transform: rotate(45deg);
  -ms-transform: rotate(45deg);
  transform: rotate(45deg);
}

#vi_welcome_close:after {
  -webkit-transform: rotate(135deg);
  -ms-transform: rotate(135deg);
  transform: rotate(135deg);
}

#vi_welcome_close:before,
#vi_welcome_close:after {
  content: "";
  position: absolute;
  display: block;
  background-color: #fff;
  top: 10px;
  left: 3px;
  width: 16px;
  height: 1px;
}

