    font-size: 21px;
    line-height: 25px;
  }
}

.dockCard__content__inner {
    margin: 0px !important;
}

.dockCard__nowrap {
  white-space: nowrap;
}

.dockCard__Container {
    display: flex;
    height: 100%;
    flex-direction: column;
    justify-content: center;
}

.dockCard__image__wrap {
    align-self: flex-start !important;

    @media screen and (min-width: 700px) {
      display: block !important;
    }   
