.welcomeAdLayout{
    padding: 0
}
.welcomeAdLayout__Text{
    margin: 0
}
.welcomeAdLayout__image__wrap{
    display: none;
}
.welcomeAdLayout__button{
    display: none;
}
.welcomeAdLayout__nowrap{
    white-space: nowrap;
}
.welcomeAdLayout__Container{
    height: 225px;
    padding-top: 100px;
    padding-bottom: 100px;
    display: flex;
    flex-direction: column;
    justify-content: space-between;
    align-items: center;
}
.welcomeAdLayout__Custom__Button{
    display: block;
    width: 108px;
    background-color: #000;
    color: #fff;
    font-size: 11px;
    font-weight: 800;
    border-radius: 3px;
    font-family: nyt-franklin;
    padding-right: 8px;
    padding-left: 8px;
    padding-top: 9px;
    padding-bottom: 9px;
    text-align: center;
}
.welcomeAdLayout__Button__Link{
    text-decoration: none;
}
.welcomeAdLayout__Copy{
    display: flex;
    flex-direction: column;
    align-items: center;
}
.welcomeAdLayout__Copy__Top{
    font-size: 11px;
    font-weight: 800;
    color: #fff;
    background-color: #000;
    padding: 4px 7px;
    @media screen and (min-width: 375px){
        font-size: 13px;
        padding: 8px 10px;
    }
}
.welcomeAdLayout__Copy__Offer{
    font-size: 16.5px;
    font-weight: 500;
    background-color: #f3957d;
    padding: 11px 9px;
    @media screen and (min-width: 375px){
        font-size: 18px;
        padding: 15px 12px;
    }
}
.welcomeAdLayout__Copy__Bottom{
    font-size: 13px;
    font-weight: 600;
    background-color: #fff;
    padding: 5px 16px;
    font-style: italic;
    @media screen and (min-width: 375px){
        font-size: 15px;
        padding: 11px 22px;
    }
}
.welcomeAdLayout__IMG__Wrap{
    display:flex;
    justify-content: center;
}
.welcomeAdLayout__IMG{
    width: 400px;
    max-width: 87%
}
.welcomeAdLayout__Logo{
    height: 27px;
}
.welcomeAdLayout__Login{
    font-size: 11px;
    color: #000;
    position: absolute;
    left: 20px;
    top: 20px;
    a {
        color: #000;
    }
    @media screen and (min-width: 1024px){
        left: auto;
        right: 31px;
        top: 41px;
    }
}
@media screen and (min-width: 767px){
    .welcomeAdLayout{
        height: 320px;
    }
    .welcomeAdLayout__Container{
        height: 220px;
        padding-top: 50px;
         padding-bottom: 50px;
    }
}
@media screen and (-ms-high-contrast: active), screen and (-ms-high-contrast: none) {  
   /* IE10+ specific styles go here */ 

   .welcomeAdLayout__image__wrap{
       min-height: 1px;
   }
   .welcomeAdLayout__content__inner{
       min-height: 1px;
   }

}

